mapper.select:
SELECT * 
FROM SYS_KEYSTONE a
WHERE ssoid = #{ssoid}

mapper.update:
UPDATE SYS_KEYSTONE a SET
	a.userid = #{userid},
	a.username = #{username},
	a.usertoken = #{usertoken},
	a.projectid = #{projectid}
WHERE a.ssoid = #{ssoid}

UPDATE SYS_KEYSTONE a SET
	a.usertoken = #{usertoken},
	a.projectid = #{projectid}
WHERE a.ssoid = #{ssoid}

mapper.delete:
DELETE FROM SYS_KEYSTONE

	
		WHERE 1 != 1
	
	
		WHERE ssoid = #{ssoid}
	
	
		WHERE ssoid in #{ssoid}
